import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const Header = styled.header`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 50px;
  display: flex;
  align-items: center;
  background-color: rgba(20, 20, 20, 0.8);
  z-index: 10;
  box-shadow: 0px 1px 5px 2px rgba(0, 0, 0, 0.8);
`;

const List = styled.ul`
  display: flex;
`;

const Item = styled.li`
  width: 80px;
  height: 50px;
  text-align: center;
  padding-top: 20px;
  border-bottom: 4px solid ${props => (props.current ? "" : "transparent")};
`;

const sLink = styled(Link)`
  height: 50px;
  display: flex;
  align-items: center;
`;

export default () => (
  <Header>
    <List>
      <Item current={false}>
        <sLink to="/">Movies</sLink>
      </Item>
      <Item current={true}>
        <sLink to="/tv">TV</sLink>
      </Item>
      <Item current={false}>
        <sLink to="/search">Search</sLink>
      </Item>
    </List>
  </Header>
);
